import React from "react";
import "./App.css";
import useExchange from "./data/useExchange";
import { crypto, securitiesHiRisk } from "./data/data";

const App = () => {
  const rates = useExchange();
  const btcAmount = (1 / rates.BTC) * crypto.amount;
  const securitiesHiRiskAmount = (1 / rates.EUR) * securitiesHiRisk.amount;
  const totalUSD = btcAmount + securitiesHiRiskAmount;
  const totalUAH = (btcAmount + securitiesHiRiskAmount) * rates.UAH;

  return <div className="App">Total: {totalUSD.toFixed(2)} USD</div>;
};

export default React.memo(App);

