import { securities, deposits, crypto } from "./data";
import useExchange from "./useExchange";

const Type = {
  securities: securities,
  deposits: deposits,
  crypto: crypto
};

const AssetsVolume = (Type) => {
  const rates = useExchange();
  const assetsReducer = Type.reduce((acc, item) => {
    return acc + (1 / rates[item.currency]) * item.amount;
  }, 0);
  const assetsAmount = assetsReducer;
  return assetsAmount;
};

export default AssetsVolume;
