import { deposits } from "./data";
import useExchange from "./useExchange";

const DepositsExchange = () => {
  const rates = useExchange();
  const securitiesReducer = deposits.reduce((acc, item) => {
    return acc + item.amount;
  }, 0);
  const securitiesAmount = (1 / rates.UAH) * securitiesReducer;
  return securitiesAmount;
};

export default DepositsExchange;
