import { deposits } from "./data";
import useExchange from "./useExchange";

const DepositsExchange = () => {
  const rates = useExchange();
  const depositsReducer = deposits.reduce((acc, deposit) => {
    return acc + deposit.amount;
  }, 0);
  const depositsAmount = (1 / rates.UAH) * depositsReducer;
  return depositsAmount;
};

export default DepositsExchange;
