import { crypto } from "./data";
import useExchange from "./useExchange";

const CryptoExchange = () => {
  const rates = useExchange();
  const cryptoAmountReducer = crypto.reduce((acc, item) => {
    return acc + item.amount;
  }, 0);
  const cryptoCurrencyReducer = crypto.reduce((acc, item) => {
    return acc + item.currency;
  }, 0);
  console.log("Crypto: ", cryptoCurrencyReducer);

  const cryptoAmount = (1 / rates.EUR) * cryptoAmountReducer;
  return cryptoAmount;
};

export default CryptoExchange;
