import React from "react";
import "./App.css";
import useExchange from "./data/useExchange";
import AssetsVolume from "./data/AssetsVolume";
import { assets } from "./data/data";

const App = () => {
  const rates = useExchange();
  // const total =
  //   AssetsVolume(assets.crypto) +
  //   AssetsVolume(assets.securities) +
  //   AssetsVolume(assets.deposits);
  const total = Object.keys(assets)
    .map((item) => AssetsVolume(assets[item]))
    .reduce((acc, item) => {
      return acc + item;
    });
  const totalUAH = total * rates.UAH;
  console.log(
    total.reduce((acc, item) => {
      return acc + item;
    })
  );

  return (
    <div className="App">
      {isNaN(total) ? "Loading..." : `Total: ${total.toFixed(2)} USD`}
      <br />
      <br />
      {isNaN(totalUAH) ? "Loading..." : `Total: ${totalUAH.toFixed(2)} UAH`}
    </div>
  );
};

export default React.memo(App);

