import useExchange from "./useExchange";

const DepositsInterest = (asset) => {
  const rates = useExchange();
  return asset.reduce((acc, item) => {
    return acc + (1 / rates[item.currency]) * item.amount;
  }, 0);
};

export default DepositsInterest;
